import { FaPlay } from "react-icons/";

function Movie({ movie, onClick }) {
  const title = movie.Title;
  const fixedTitle = title.trim().split(/\s+/);

  const shortenedTitle =
    fixedTitle.length > 5 ? fixedTitle.slice(0, 5).join(" ") + "..." : title;
  return (
    <li onClick={onClick} className="w-64 max-w-64 flex flex-col gap-2 pt-5">
      <div className="relative">
        <img
          src={movie.Poster}
          alt={`${movie.Title} poster`}
          className="w-full max-w-64 h-96 object-cover"
        />

        <button
          className="absolute inset-0 flex items-center justify-center text-white opacity-0 hover:opacity-100 transition-opacity bg-black bg-opacity-50"
          aria-label="Play"
        >
          <FaPlay size={30} />
        </button>
      </div>
      <h3 className="text-m font-sm tracking-tighter text-yellow w-62 max-w-62">
        {shortenedTitle}
      </h3>

      <div>
        <p>
          <span>ðŸ—“</span>
          <span className="text-white">{movie.Year}</span>
        </p>
      </div>
    </li>
  );
}

export default Movie;
