import { createContext, useContext, useState, useEffect } from "react";

const WatchlistContext = createContext();

export function WatchlistProvider({ children }) {
  const [watchlist, setWatchlist] = useState([]);
  const [watchedlist, setWatchedlist] = useState([]);
  useEffect(() => {
    const savedWatchlist = JSON.parse(localStorage.getItem("watchlist")) || [];
    const savedWatchedlist =
      JSON.parse(localStorage.getItem("watchedlist")) || [];
    setWatchlist(savedWatchlist);
    setWatchedlist(savedWatchedlist);
  }, [watchlist, watchedlist]);
  useEffect(() => {
    localStorage.setItem("watchlist", JSON.stringify(watchlist));
  }, [watchlist]);
  useEffect(() => {
    localStorage.setItem("watchedlist", JSON.stringify(watchedlist));
  }, [watchlist]);

  function addToWatchlist(movie) {
    setWatchlist((prevList) => {
      return prevList.some((m) => m.imdbID === movie.imdbID)
        ? prevList
        : [...prevList, movie];
    });
  }
  function addToWatched(movie) {
    setWatchlist((prevList) =>
      prevList.filter((mov) => mov.imdbID !== movie.imdbID)
    );
  }

  return (
    <WatchlistContext.Provider value={{ watchlist, addToWatchlist }}>
      {children}
    </WatchlistContext.Provider>
  );
}

export function useWatchlist() {
  return useContext(WatchlistContext);
}
