import { useWatchlist } from "../context/WatchlistContext";

function WatchedMovies() {
  const { watchedlist } = useWatchlist();
  return (
    <div className="w-1/3  bg-yellow p-4">
      <h3 className="text-purple font-bold text-xl text-center">
        WATCHED MOVIES
      </h3>
      <ul>
        {watchedlist.map((movie) => (
          <li key={movie.imdbID} className=" flex  gap-8 pt-5  items-start">
            <img
              src={movie.Poster}
              alt={`${movie.Title} poster`}
              className="w-20 h-22"
            />
            <div className="flex flex-col w-2/4">
              <h4 className="text-yellow font-bold">{movie.Title}</h4>
              <p className="text-yellow font-semibold">{movie.Year}</p>

              <p className="text-white text-sm">{movie.Plot}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default WatchedMovies;
