import { useWatchlist } from "../context/WatchlistContext";

function WatchedMovies() {
  const { watchedlist } = useWatchlist();
  return (
    <div className="w-1/3  bg-yellow p-4">
      <div className="flex justify-between">
        <h3 className="text-purple font-bold text-xl text-center">
          WATCHED MOVIES
        </h3>
        {watchedlist ? (
          <h3 className="text-black font-bold text-xl text-center">
            TOTAL: {watchedlist.length} films
          </h3>
        ) : null}
      </div>
      <ul>
        {watchedlist.map((movie) => (
          <li key={movie.imdbID} className=" flex  gap-8 pt-5  items-start">
            <img
              src={movie.Poster}
              alt={`${movie.Title} poster`}
              className="w-20 h-22"
            />
            <div className="flex flex-col ">
              <h4 className="text-black font-semibold">{movie.Title}</h4>
              <p className="text-black font-semibold">{movie.Year}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default WatchedMovies;
