import { useWatchlist } from "../context/WatchlistContext";
import LinkButton from "./LinkButton";

function MoviesToWatch() {
  const { watchlist } = useWatchlist();
  console.log(watchlist);
  if (watchlist.length === 0)
    return <p>Your movie list is empty, add some movies to watch.</p>;
  return (
    <div className="w-2/3 p-4">
      <h3 className="text-yellow font-bold text-xl text-center">
        MOVIES TO WATCH
      </h3>
      <ul className="flex flex-wrap gap-4 justify-start align-top">
        {watchlist.map((movie) => (
          <li key={movie.imdbID} className="w-full flex  gap-2 pt-5 align-top">
            <img
              src={movie.Poster}
              alt={`${movie.Title} poster`}
              className="w-28 h-28"
            />
            <div className="flex flex-col">
              <h4 className="text-yellow font-bold">{movie.Title}</h4>
              <p className="text-yellow font-semibold">{movie.Year}</p>

              <p className="text-white text-sm">{movie.Plot}</p>
            </div>
            <LinkButton className="text-black bg-yellow py-2 px-4 font-semibold inline-block">
              Add to watched
            </LinkButton>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MoviesToWatch;
