import { QueryProvider } from "../context/queryContext";
import SearchBar from "./SearchBar";
import MoviesList from "./MoviesList";
function Main() {
  return (
    <QueryProvider>
      <div className="bg-purple flex flex-col items-center justify-center gap-4 w-full h-full p-12">
        <h2 className="text-white text-xl">
          Search for your next film to watch
        </h2>
        <SearchBar />
        <MoviesList />
      </div>
    </QueryProvider>
  );
}

export default Main;
