import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import LinkButton from "./LinkButton";

const KEY = "ef5bebce";

function MovieDetailPage() {
  const { id } = useParams();

  const [movie, setMovie] = useState(null);

  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const controller = new AbortController();
    async function fetchMovie() {
      try {
        setIsLoading(true);
        setError("");

        const res = await fetch(
          `http://www.omdbapi.com/?i=${id}&apikey=${KEY}`,
          {
            signal: controller.signal,
          }
        );

        if (!res.ok)
          throw new Error("Something went wrong with fetching movies");

        const data = await res.json();

        if (data.Response === "False") throw new Error(data.Error);

        setMovie(data);
      } catch (err) {
        if (err.name !== "AbortError") {
          setError(err.message);
        }
      } finally {
        setIsLoading(false);
      }
    }

    fetchMovie();

    return () => {
      controller.abort();
    };
  }, [id]);

  if (isLoading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!movie) return <p>Movie is loading...</p>;

  return (
    <div className="flex w-2/3 gap-4 text-white">
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <div className="flex flex-col gap-2">
        <h1 className="text-3xl text-yellow ">{movie.Title}</h1>
        <div className="flex gap-4">
          <p className="font-bold text-xl pb-6">üé¨ {movie.Year}</p>
          {movie.imdbRating === "N/A" ? null : (
            <p className="font-bold text-xl"> ‚≠êÔ∏è{movie.imdbRating}</p>
          )}
        </div>
        <p className="font-bold">{movie.Actors}</p>

        <p className="text-sm pb-6">{movie.Plot}</p>
        <div className="flex gap-6">
          <LinkButton
            to="/mylist"
            className="text-black bg-yellow w-max font-bold border-2 border-solid border-yellow inline-block px-4 py-2"
          >
            Add to watch
          </LinkButton>
          <LinkButton
            to="-1"
            className="text-white w-max font-bold border-2 border-solid border-white inline-block px-4 py-2"
          >
            Return back
          </LinkButton>
        </div>
      </div>
    </div>
  );
}

export default MovieDetailPage;
